package practice;

public class star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=5;i++)
{
	for(int j=1;j<=i;j++)
	{
		System.out.print("*");
	}
	System.out.print("\n");
}

System.out.println("---------------");
for(int k=5;k>=1;k--)
{
	for(int l=k;l>=1;l--)
	{
		
		System.out.print("*");
	}
	System.out.print("\n");

}

	}
	
	
	

}
